//通过mutation间接更新state的多个对象 

import {
    RECEIVE_POSTS,
    RECEIVE_PROFILE
} from './mutations-type'

import {
    reqPosts,
    reqProfile
} from '../api'

 export default {
    //异步获取POSTS
    async getPosts ({commit,state}){
        //发送异步ajax请求
        console.log(state)
       const result = await reqPosts()
        //提交mutations
        const posts = result
        // console.log(posts) 
        commit(RECEIVE_POSTS,{posts})
    },

    async getProfile ({commit,state}){
        //发送异步ajax请求
        console.log(state)
       const result = await reqProfile()
        //提交mutations
        const profile = result
        // console.log(posts) 
        commit(RECEIVE_PROFILE,{profile})
    }
 }
