//通过mutation间接更新state的多个对象 

import {
    RECEIVE_POSTS
} from './mutations-type'

import {
    reqPosts
} from '../api'

 export default {
    //异步获取POSTS
    async getPosts ({commit,state}){
        //发送异步ajax请求
       const result = await reqPosts()
        //提交mutations
        const posts = result.data
        commit(RECEIVE_POSTS,{posts})
    }
 }
